sys_script_include.script:
var lmsAjax = Class.create();
lmsAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getDateDiff: function() {
        var d1 = new GlideDate();
        d1.setDisplayValue(this.getParameter('sysparm_start'));
        var d2 = new GlideDate();
        d2.setDisplayValue(this.getParameter('sysparm_end'));

        var duration = GlideDate.subtract(d1, d2);
        return duration.getNumericValue();
    },

    getLeaveBucket: function() {
        var leaveDetails = {};
        //gs.addInfoMessage('inside getleave function 01', 'ISL-LMS15');
        var emp = this.getParameter('sysparm_user');
        var l_type = this.getParameter('sysparm_leavetype');
        //gs.addInfoMessage('user combo:' + emp + '-' + xyz);

        var gr = new GlideRecordSecure('x_945729_lmss_leave_bucket');
        gr.addQuery('employee', '=', emp.toString());
        gr.addQuery('leave_type', '=', l_type.toString());
        gr.query();
        //gs.addInfoMessage('just queried 02 --> ' + gr.getRowCount(), 'ISL-LMS23');
        if (gr.next()) {
            //gs.addInfoMessage('inside IF 03', 'ISL-LMS25');
            //var leaveDetails = {};
            leaveDetails.accrued = '' + gr.accrued;
            //gs.addInfoMessage('accrued: ' + gr.accrued, 'ISL-LMS28');
            leaveDetails.balance = '' + gr.balance;
            leaveDetails.taken = '' + gr.taken;

        } else {
            leaveDetails.accrued = 0;
            //gs.addInfoMessage('accrued: ' + gr.accrued, 'ISL-LMS28');
            leaveDetails.balance = 0;
            leaveDetails.taken = 0;
        }
        return JSON.stringify(leaveDetails);

    },




    type: 'lmsAjax'
});
